import { StatusBar } from 'expo-status-bar';
import React,  { useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, Image, ImageBackground, Component } from 'react-native';
import logo from './assets/galo.png';
import winnerlogo from './assets/galo2.png';
import bola from './assets/bola.png';
import xis from './assets/x.png';

export default function App() {

  const [screen, setScreen] = useState('menu');
  const [player1, setPlayer1] = useState('');
  const [player2, setPlayer2] = useState('');
  const [table, setTable] = useState([]);
  const [last, setLast] = useState(0);
  const [winner, setWinner] = useState('');

  const [point, setPoint] = useState('');

  const [turn, setTurn] = useState('');

  switch (screen) {
    case 'menu':
      return getTelaMenu();
      break;
    case 'game':
      return getTelaGame();
      break;
    case 'winner':
      return getTelaWinner();
      break;

    default:
      break;
  }

  function StartGame(player){
    setPlayer1(player);
    if(player === 'X'){ 
      setPlayer2('O');
    }

    if(player === 'O'){ 
      setPlayer2('X');
    }

    setLast(9);
    setTable([
      ['','',''],
      ['','',''],
      ['','',''],
    ]);

    setScreen('game');
  }


  function enemyPlaying(player){
    console.log(turn);
    if(turn === 'enemy'){
      setTimeout(() => {
        // table.map((line, numberline) => {
        //   line.map((column, numberColumn )=> {
        let RandomNumberLine = Math.floor(Math.random() * 4 ) ;
        let RandomNumberColumn = Math.floor(Math.random() * 4 ) ;
        console.log(RandomNumberColumn );
        console.log(RandomNumberLine );
        play(RandomNumberColumn, RandomNumberLine, player);
        setTurn('player');
        //   })
        }, 3000);
    }
  }

  function play(column, line, player){
    table[line][column] = player;
    setTable([...table]);

    console.log(column);
    console.log(line);

    setPlayer1(player === 'X' ? 'O' : 'X');
    checkWinner(table, line, column);
    setTurn('enemy');
    enemyPlaying('player2');
  }

  function getTelaMenu(){
    return (
      <View style={styles.container}>
        <Image source={logo} style={{ width: 200, height: 200 }} /> 
        <Text style={styles.title}>Jogo do Galo</Text>
        <Text style={styles.subtitle}>Selecione o Jogador</Text>
        <View style={styles.containerPlayer}>
          <TouchableOpacity style={styles.touchable} onPress={()=>StartGame('X')}>
            <ImageBackground source={xis} resizeMode="cover" style={styles.image}> <Text  style={styles.playerX}> </Text></ImageBackground>
          </TouchableOpacity>
          <TouchableOpacity style={styles.touchable} onPress={()=>StartGame('O')}>
            <ImageBackground source={bola} resizeMode="cover" style={styles.image}> <Text  style={styles.playerO}> </Text></ImageBackground>
          </TouchableOpacity>
        </View>
        <StatusBar style="auto" />
      </View>
    );
  }

  function getTelaGame(){
    return (
      <View style={styles.container}>
        <Text style={styles.title}>Jogo do galo</Text>

        {
          table.map((line, numberline) => {
            return (
              <View key={numberline} style={styles.containerPlayer}>
                {line.map((column,numberColumn) => {
                  return(
                    <TouchableOpacity 
                    key={numberColumn} 
                    style={styles.touchable} 
                    onPress={()=>play(numberColumn,numberline, player1)}
                    disabled={column !== ''}
                    >
                      {
                        column === 'O' &&
                        <ImageBackground source={bola} resizeMode="cover" style={styles.image}> <Text style={column === 'X' ? styles.playerX : styles.playerO }></Text></ImageBackground>
                      }
                      {   
                        column === 'X' &&
                        <ImageBackground source={xis} resizeMode="cover" style={styles.image}> <Text style={column === 'X' ? styles.playerX : styles.playerO }></Text></ImageBackground>
                      }
                    </TouchableOpacity>
                  )
                })}
              </View>
            )
          })
        }

        <TouchableOpacity style={styles.backtoMenu} onPress={()=>setScreen('menu')}>
          <Text style={styles.backtoMenuText} >Back to Menu</Text>
        </TouchableOpacity>
        <StatusBar style="auto" />
      </View>
    );
  }

  function checkWinner(table, line, column){

    //linha
    if(table[line][0] !== '' && table[line][0] === table[line][1] && table[line][1] === table[line][2]){
      return endGame(table[line][0]);
    }

     //coluna
    if(table[0][column] !== '' && table[0][column] === table[1][column] && table[1][column] === table[2][column]){
      return endGame(table[0][column]);
    }

    //diagonal1
    if(table[0][0] !== '' && table[0][0] === table[1][1] && table[1][1] === table[2][2]){
      return endGame(table[0][0]);
    }

    //diagonal2
    if(table[0][2] !== '' && table[0][2] === table[1][1] && table[1][1] === table[2][0]){
      return endGame(table[0][2]);
    }

    //nenhum ganhador
    if(last - 1 === 0){
      setPoint(0);
      return endGame('');
    }

    setLast((last - 1));
  }

  function endGame(player){
    setWinner(player);
    setPoint(10);
    setScreen('winner');
  }

  function getTelaWinner(){
    return (
      <View style={styles.container}>
        <Text style={styles.title}>Jogo do Galo</Text>
        <View style={styles.containerwinner}>
         {

          winner === '' && 
          <><View style={styles.container}><Image source={logo} style={{ width: 300, height: 300 }} />
          <Text style={styles.winner}>GALO!</Text></View></>
         }
         {
          winner !== '' && 
          <>
           <View style={styles.container}>
            {/* <ConfettiCannon count={200} origin={{x: -10, y: 0}} /> */}
             <Image source={winnerlogo} style={{ width: 300, height: 300 }} />
             <Text style={styles.point}>Points: {point}</Text>
              <View style={styles.touchable}>
              {
                winner === 'O' &&
                <>
                <ImageBackground source={bola} resizeMode="cover" style={styles.image}>
                  <Text style={winner === 'X' ? styles.playerX : styles.playerO }></Text> 
                </ImageBackground></>
              }
              {
                winner === 'X' &&
                <>
                <ImageBackground source={xis} resizeMode="cover" style={styles.image}>
                  <Text style={winner === 'X' ? styles.playerX : styles.playerO }></Text>
                </ImageBackground></>
              }
                {/* <Text style={winner === 'X' ? styles.playerX : styles.playerO }>{winner}</Text> */}
              </View>
            </View>
          </>

         }
          <TouchableOpacity style={styles.backtoMenu} onPress={()=>setScreen('menu')}>
            <Text style={styles.backtoMenuText} >Back to Menu</Text>
          </TouchableOpacity>
        </View>
        <StatusBar style="auto" />
      </View>
    );
  }

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  backtoMenu: {
    marginTop: 20,
    backgroundColor: '#ff00009e',
    padding: 20,
    borderRadius: 8
  },
  backtoMenuText: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center'
  },
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    color: "#333"
  },
  subtitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: "#333",
    marginTop: 10
  },
  touchable: {
    width: 80,
    height: 80,
    backgroundColor: '#ddd',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 8,
    marginTop: 20
  },
  playerO: {
    fontSize: 40,
    color: 'red',
    fontWeight: 'bold'
  },
  playerX: {
    fontSize: 40,
    color: 'blue',
    fontWeight: 'bold',
  },
  containerPlayer: {
    flexDirection: 'row',
    gap: 10
  },
  containerwinner: {
    flexDirection: 'column',

    gap: 10
  },
  image: {
    justifyContent: "center",
    width:80,
    height:80,
  },
  point: {
    fontSize: 20,
    fontWeight: 'bold',
    color: "#333",
  }
});
