import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';

export default function App() {

  const [tela, setTela] = useState('menu');

  switch (tela) {
    case 'menu':
      return getTelaMenu();
      break;
    case 'game':
      return getTelaGame();
      break;
    case 'winner':
      return getTelaWinner();
      break;

    default:
      break;
  }

  function getTelaMenu(){
    return (
      <View style={styles.container}>
        <Text style={styles.title}>Jogo do Galo</Text>
        <Text style={styles.subtitle}>Selecione o Jogador</Text>

        <TouchableOpacity style={styles.touchable} >
          <Text  style={styles.playerX}>X</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.touchable}>
          <Text  style={styles.playerO}>O</Text>
        </TouchableOpacity>
        <StatusBar style="auto" />
      </View>
    );
  }

  function getTelaGame(){
    return (
      <View style={styles.container}>
        <Text>Game</Text>
        <StatusBar style="auto" />
      </View>
    );
  }

  function getTelaWinner(){
    return (
      <View style={styles.container}>
        <Text>Winner</Text>
        <StatusBar style="auto" />
      </View>
    );
  }

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    color: "#333"
  },
  subtitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: "#333",
    marginTop: 20
  },
  touchable: {
    width: 80,
    height: 80,
    backgroundColor: '#ddd',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  playerO: {
    fontSize: 40,
    color: 'red',
    fontWeight: 'bold'
  },
  playerX: {
    fontSize: 40,
    color: 'blue',
    fontWeight: 'bold'
  }
});
