import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';

export default function App() {

  const [screen, setScreen] = useState('menu');
  const [player1, setPlayer1] = useState('');
  const [table, setTable] = useState([]);
  const [last, setLast] = useState(0);
  const [winner, setWinner] = useState('');

  switch (screen) {
    case 'menu':
      return getTelaMenu();
      break;
    case 'game':
      return getTelaGame();
      break;
    case 'winner':
      return getTelaWinner();
      break;

    default:
      break;
  }

  function StartGame(player){
    setPlayer1(player);

    setLast(9);
    setTable([
      ['','',''],
      ['','',''],
      ['','',''],
    ]);

    setScreen('game');
  }

  function getTelaMenu(){
    return (
      <View style={styles.container}>
        <Text style={styles.title}>Jogo do Galo</Text>
        <Text style={styles.subtitle}>Selecione o Jogador</Text>
        <View style={styles.containerPlayer}>
          <TouchableOpacity style={styles.touchable} onPress={()=>StartGame('X')}>
            <Text  style={styles.playerX}>X</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.touchable} onPress={()=>StartGame('O')}>
            <Text  style={styles.playerO}>O</Text>
          </TouchableOpacity>
        </View>
        <StatusBar style="auto" />
      </View>
    );
  }

  function getTelaGame(){
    return (
      <View style={styles.container}>
        <Text>Jogo do galo</Text>

        {
          table.map((line, numberline) => {
            return (
              <View key={numberline} style={styles.containerPlayer}>
                {line.map((column,numberColumn) => {
                  return(
                    <TouchableOpacity style={styles.touchable} onPress={()=>StartGame('X')}>
                      <Text style={column === 'X' ? styles.playerX : styles.playerY }>X</Text>
                     </TouchableOpacity>
                  )
                })}
              </View>
            )
          })
        }
        <StatusBar style="auto" />
      </View>
    );
  }

  function getTelaWinner(){
    return (
      <View style={styles.containerPlayer}>
        <Text>Winner</Text>
        <StatusBar style="auto" />
      </View>
    );
  }

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    color: "#333"
  },
  subtitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: "#333",
    marginTop: 10
  },
  touchable: {
    width: 80,
    height: 80,
    backgroundColor: '#ddd',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 8,
    marginTop: 20
  },
  playerO: {
    fontSize: 40,
    color: 'red',
    fontWeight: 'bold'
  },
  playerX: {
    fontSize: 40,
    color: 'blue',
    fontWeight: 'bold'
  },
  containerPlayer: {
    flexDirection: 'row',
    gap: 10
  },
});
